import React, { useState, useEffect } from "react";
import "./HealthTips.css";
import Confetti from "react-confetti";

const HealthTips = () => {
  const [currentView, setCurrentView] = useState("main");
  const [quizStep, setQuizStep] = useState(0);
  const [quizAnswers, setQuizAnswers] = useState({});
  const [showConfetti, setShowConfetti] = useState(false);

  const questions = [
    {
      question: "What is your current lifestyle?",
      options: ["Active", "Moderate", "Sedentary"],
      key: "lifestyle",
    },
    {
      question: "How many hours do you sleep?",
      options: ["4-5 hours", "6-8 hours", "8+ hours"],
      key: "sleep",
    },
    {
      question: "How many glasses of water do you drink daily?",
      options: ["1-3", "4-6", "7+"],
      key: "water",
    },
    {
      question: "Write your personal health goal:",
      isText: true,
      key: "goal",
    },
  ];

  const handleAnswer = (value) => {
    const key = questions[quizStep].key;
    setQuizAnswers({ ...quizAnswers, [key]: value });

    if (quizStep < questions.length - 1) {
      setQuizStep(quizStep + 1);
    } else {
      setShowConfetti(true);
    }
  };

  const resetQuiz = () => {
    setQuizStep(0);
    setQuizAnswers({});
    setShowConfetti(false);
  };

  return (
    <div className="health-tips">

      {showConfetti && <Confetti />}

      {/* HEADER */}
      <div className="health-header">
        <h1>Health Tips & Wellness</h1>
        <p>Your personalized health improvement assistant</p>
      </div>

      {/* NAV BUTTONS */}
      <div className="health-nav">
        <button
          className={currentView === "main" ? "active" : ""}
          onClick={() => setCurrentView("main")}
        >
          Home
        </button>
        <button
          className={currentView === "dailyTips" ? "active" : ""}
          onClick={() => setCurrentView("dailyTips")}
        >
          Daily Tips
        </button>
        <button
          className={currentView === "quiz" ? "active" : ""}
          onClick={() => setCurrentView("quiz")}
        >
          Health Quiz
        </button>
      </div>

      {/* MAIN VIEW */}
      {currentView === "main" && (
        <div className="main-view">
          <div className="feature-cards">

            <div className="feature-card" onClick={() => setCurrentView("dailyTips")}>
              <div className="feature-icon">üí°</div>
              <h3>Daily Health Tips</h3>
              <p>Simple lifestyle tips to improve your day-to-day health.</p>
            </div>

            <div className="feature-card" onClick={() => setCurrentView("quiz")}>
              <div className="feature-icon">üìù</div>
              <h3>Health Quiz</h3>
              <p>Get personalized suggestions based on your habits.</p>
            </div>

          </div>
        </div>
      )}

      {/* DAILY TIPS */}
      {currentView === "dailyTips" && (
        <div className="daily-tips">
          <h3>Daily Health Tips</h3>

          <div className="tips-grid">
            <div className="tip-card">
              <div className="tip-icon">üö∂‚Äç‚ôÄÔ∏è</div>
              <h4>Walk 20 Minutes</h4>
              <p>Walking daily improves mental and physical fitness.</p>
            </div>

            <div className="tip-card">
              <div className="tip-icon">üíß</div>
              <h4>Drink More Water</h4>
              <p>Hydrating keeps your skin glowing and brain active.</p>
            </div>
          </div>
        </div>
      )}

      {/* QUIZ */}
      {currentView === "quiz" && (
        <div className="quiz-container">

          <div className="quiz-progress">
            <span>Step {quizStep + 1} of {questions.length}</span>
          </div>

          <h2>{questions[quizStep].question}</h2>

          {!questions[quizStep].isText ? (
            questions[quizStep].options.map((opt, i) => (
              <button
                key={i}
                className="option-btn"
                onClick={() => handleAnswer(opt)}
              >
                {opt}
              </button>
            ))
          ) : (
            <div className="text-input">
              <input
                type="text"
                placeholder="Write here..."
                onChange={(e) => setQuizAnswers({ ...quizAnswers, goal: e.target.value })}
              />
              <button onClick={() => handleAnswer(quizAnswers.goal)}>Next</button>
            </div>
          )}

          {quizStep === questions.length - 1 && showConfetti && (
            <div className="result-screen">
              <h2>üéâ Your Personalized Health Plan</h2>
              <p>Based on your responses, we prepared helpful suggestions.</p>

              <button className="retry-btn" onClick={resetQuiz}>
                Retry Quiz
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default HealthTips;
